import React, { useState } from 'react';
import Hero from '../components/Hero';
import Categories from '../components/Categories';
import PremiumStones from '../components/PremiumStones';
import PopularStones from '../components/PopularStones';
import KeyMetrics from '../components/KeyMetrics';
import Awards from '../components/Awards';
import Testimonials from '../components/Testimonials';
import Clients from '../components/Clients';
import LatestNews from '../components/LatestNews';
import WhatsAppModal from '../components/WhatsAppModal';
import { FaWhatsapp } from 'react-icons/fa';

export default function Home() {
  const [isModalOpen, setIsModalOpen] = useState(false);

  return (
    <>
      <Hero />
      <Categories />
      <PremiumStones />
      <PopularStones />
      <KeyMetrics />
      <Awards />
      <Testimonials />
      <Clients />
      <LatestNews />
      <button
        onClick={() => setIsModalOpen(true)}
        className="flex items-center px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 transition duration-200"
      >
        <FaWhatsapp className="mr-2" />
        Contact Sales via WhatsApp
      </button>
      <WhatsAppModal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />
    </>
  );
}
